const data = $input.all()[0].json;

// Ensure email is a non-empty string
if (!data.email) {
  // If email is missing, stop the workflow for this item
  console.log('Stopping workflow: email is missing.');
  return [];
}

// Create a new object to avoid modifying the original input
const outputData = { ...data };

// Ensure email and blogId are strings
outputData.email = String(outputData.email);
outputData.blogId = String(outputData.blogId || 'Unknown Blog');

// URL-encode the parameters
const encodedEmail = encodeURIComponent(outputData.email);
const encodedBlogId = encodeURIComponent(outputData.blogId);

// Construct the review URL
outputData.reviewUrl = `http://localhost:5678/blog-review.html?email=${encodedEmail}&id=${encodedBlogId}`;

console.log('Processed data with reviewUrl:', JSON.stringify(outputData, null, 2));

return [{ json: outputData }];
